import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Login from './components/customers/Login';
import Register from './components/customers/Register';
import Dashboard from './pages/Dashboard';
import Navbar from './components/common/Navbar';
import { getUserFromToken, removeToken } from './utils/auth';

const App = () => {
  const [customer, setCustomer] = useState(() => {
    const user = getUserFromToken();
    return user ? { _id: user.id } : null;
  });

  const handleLogout = () => {
    removeToken();
    setCustomer(null);
  };

  const handleLoginRegister = (userData) => {
    setCustomer({ _id: userData._id });
  };

  return (
    <Router>
      <Navbar onLogout={handleLogout} />
      <Routes>
        <Route
          path="/"
          element={customer ? <Dashboard customer={customer} /> : <Navigate to="/login" />}
        />
        <Route
          path="/login"
          element={!customer ? <Login onLogin={handleLoginRegister} /> : <Navigate to="/" />}
        />
        <Route
          path="/register"
          element={!customer ? <Register onRegister={handleLoginRegister} /> : <Navigate to="/" />}
        />
      </Routes>
    </Router>
  );
};

export default App;
